import { useQuery } from "react-query";

import axios from "axios";

import { SingleProduct } from "../types";

function fetchProduct({ slug }: { slug: string }) {
  return axios
    .get(`http://localhost:5000/shop/${slug}`)
    .then((response) => response.data);
}

type UseDiscussionOptions = {
  slug: string | undefined;
};

export function useSingleProduct({slug} : UseDiscussionOptions) {
  return useQuery("singleProduct", () => fetchProduct({slug}));
}
