import React, { useState } from "react";
import { useParams } from "react-router-dom";
import style from "styles/SingleProduct.module.css";
import MaxContentWidth from "components/Layout/MaxWidthContainer";
import { useSingleProduct } from "./api/getSingleProduct";
import TwoColFlex from "components/Layout/TwoColFlex";
import { TSingleProduct } from "./types";
import VariantSelector from "./components/VariantSelector";

/* Uses slug in URL to fetch the product data from the API. */

const SingleProduct = () => {
  const { slug } = useParams();
  const singleProductQuery = useSingleProduct({ slug });

  const [selectedvariant, setSelectedvariant] = useState(-1);
  const [selectedVariantName, setSelectedVariantName] = useState("x");

  function updateSelectedVariant(index: any, variantName: any) {
    setSelectedvariant(index);
    setSelectedVariantName(variantName);
    alert('test' + index);
  }

  if (singleProductQuery.isLoading) {
    return <div>Loading products...</div>;
  }

  console.log(singleProductQuery.data);

  return (
    <>
      <MaxContentWidth>
        {singleProductQuery?.data?.map(
          (product: TSingleProduct, index: number) => (
            <div key={index} className={style.SingleProduct}>
              <TwoColFlex>
                <div className={style.imageCol}>
                  <img src={product.mainImage} alt={product.productname} />
                </div>
                <div className={style.productInfoCol}>
                  {product.categoryName && (
                    <div className={style.categoryName}>
                      {product.categoryName}
                    </div>
                  )}

                  <h1 className={style.productTitle}>{product.productname}</h1>

                  <div className={style.reviews}>[reviews]</div>

                  {product.tagline && (
                    <div className={style.tagline}>{product.tagline}</div>
                  )}

                  {product.productdescription && (
                    <div className={style.description}>
                      {product.productdescription}
                    </div>
                  )}

                  <div className={style.flavor}>
                    <strong>Flavor:</strong> Peppermint
                  </div>

                  <VariantSelector data={product.productVariants} setVariant={updateSelectedVariant} currentVariant={selectedvariant} currentVariantName={selectedVariantName} />
                </div>
              </TwoColFlex>
            </div>
          )
        )}
      </MaxContentWidth>
    </>
  );
};

export default SingleProduct;
