import React from "react";

interface H1Props {
  size: string;
  text: string;
  color: string;
  class?: string;
}

const Heading = (props: H1Props) => {

  if(props.size === 'h1'){
      return <h1 className={props.class} style={{color: props.color}}>{props.text}</h1>;
  } else{
    return <h1 className={props.class} style={{color: props.color}}>{props.text}</h1>;
  }
  
};

export default Heading;
