import React from "react";
import styles from "styles/ShopProductContainer.module.css";
import MaxContentWidth from "components/Layout/MaxWidthContainer";
import Heading from "components/Layout/Heading";

interface ShopProductContainerProps {
  headingText: string;
  paragraphText?: string;
  imagePrimary: string;
  imagePrimaryAltText: string;
  imageSecondary: string;
  imageSecondaryAltText: string;
}

const ShopProductContainer = (props: ShopProductContainerProps) => {
  return (
    <div className={`${styles.shopProductContainer} ${styles.backgroundColorDefault}`}>
      <MaxContentWidth>
        <div className={styles.shopProductContainerFlex}>
          <div>
            <img className={styles.imagePrimary} src={props.imagePrimary} alt="Loading Gif" />
          </div>
          <div>
            <Heading
              size="h3"
              text={props.headingText}
              class={styles.partnersFlex}
            />
            <p>{props.paragraphText}</p>
          </div>
        </div>
      </MaxContentWidth>
    </div>
  );
};

export default ShopProductContainer;
