import React, { useState } from "react";
import styles from "styles/ShopProductContainer.module.css";
import MaxContentWidth from "components/Layout/MaxWidthContainer";
import Heading from "components/Layout/Heading";
import { updateLanguageServiceSourceFile } from "typescript";

interface ShopProductContainerProps {
  headingText: string;
  paragraphText?: string;
  imagePrimary: string;
  imagePrimaryAltText: string;
  imageSecondary: string;
  imageSecondaryAltText: string;
}

const ShopProductContainer = (props: ShopProductContainerProps) => {
  const [background, setBackground] = useState("#005466");

  function updateProductContainerUI(color: string) {
    setBackground(color);
  }

  return (
    <div
      className={styles.shopProductContainer}
      style={{ backgroundColor: background }}
    >
      <MaxContentWidth>
        <div className={styles.shopProductContainerFlex}>
          <div>
            <img
              className={styles.imagePrimary}
              src={props.imagePrimary}
              alt="Loading Gif"
            />
          </div>
          <div>
            <Heading size="h3" text={props.headingText} />
            <p>{props.paragraphText}</p>
            <div className={styles.shopProductSwatches}>
              <div
                onClick={() => updateProductContainerUI("#13c0d7")}
                className={`${styles.shopProductColorSeletor} ${styles.swatchSelected}`}
                data-variant-name="Peppermint"
              >
                <div className={styles.shopProductColorSelectorSwatch} style={{backgroundColor: '#13c0d7'}}></div>
              </div>
              <div
                onClick={() => updateProductContainerUI("#d53f23")}
                className={`${styles.shopProductColorSeletor}`}
                data-variant-name="Peppermint"
              >
                <div className={styles.shopProductColorSelectorSwatch} style={{backgroundColor: '#d53f23'}}></div>
              </div>
            </div>
          </div>
        </div>
      </MaxContentWidth>
    </div>
  );
};

export default ShopProductContainer;
