import { createSlice } from "@reduxjs/toolkit";
import { Variant } from 'features/SingleProduct/types'

type initialSliceState = { cart: Array<Variant> };
const initialState: initialSliceState = { cart: [{'variantID': 0, 'variantImage': 'test', 'variantName': 'test', 'variantPrice': 'test'}] };

export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addProductToCart: (state, productID) => {
      state.cart.push(productID.payload);
    },
    removeFromCart: (state) => {
      //remove item
    },
  },
});

export const { addProductToCart, removeFromCart } = cartSlice.actions;

// The function below is called a selector and allows us to select a value from
// the state. Selectors can also be defined inline where they're used instead of
// in the slice file. For example: `useSelector((state) => state.counter.value)`
export const selectCart = (state: any) => state.cart.cart;

export default cartSlice.reducer;
