import React, { useState } from "react";
import style from "styles/VariantSelector.module.css";
import { Variant } from "../types";

interface VariantSelectorProps {
  data: Array<Variant>;
}

const VariantSelector = (props: VariantSelectorProps) => {

function updateSelectedVariant(index: any, variantName: any){
    setSelectedvariant(index);
    setSelectedVariantName(variantName);
}

 const [selectedvariant, setSelectedvariant] = useState(props.data[0].variantID);
 const [selectedVariantName, setSelectedVariantName] = useState(props.data[0].variantName);
  return (
    <>
      <div className={style.VariantSelector}>
        {props.data.map((variant: Variant, index: number) => (
          <div key={variant.variantID} className={style.singleVariantContainer} data-id={variant.variantID}>
            <div>
              <div
                 onClick={() => updateSelectedVariant(variant.variantID, variant.variantName)} className={selectedvariant === variant.variantID ? `${style.productFormSelectorRadio} ${style.selected}` : `${style.productFormSelectorRadio}`}
              >
                <div
                  className={selectedvariant === variant.variantID ? `${style.productFormSelectorRadioInner} ${style.selected}` : `${style.productFormSelectorRadioInner}`}
                ></div>
              </div>
            </div>
            <div>{variant.variantName}</div>
          </div>
        ))}
      </div>
    </>
  );
};

export default VariantSelector;
