import React from "react";
import { useParams } from "react-router-dom";
import style from "styles/SingleProduct.module.css";
import MaxContentWidth from "components/Layout/MaxWidthContainer";
import { useSingleProduct } from "./api/getSingleProduct";
import TwoColFlex from "components/Layout/TwoColFlex";
import { TSingleProduct } from "./types";

/* Uses slug in URL to fetch the product data from the API. */

const SingleProduct = () => {
  const { slug } = useParams();

  const singleProductQuery = useSingleProduct({ slug });

  if (singleProductQuery.isLoading) {
    return <div>Loading products...</div>;
  }

  const { data } = singleProductQuery;
  console.log(data);

  return (
    <>
      <MaxContentWidth>

      {singleProductQuery.data.map((product: TSingleProduct, index: number) => (
        <div key={index} className={style.SingleProduct}>
        <TwoColFlex>
          <div className={style.imageCol}></div>
          <div className={style.productInfoCol}>
            <h1>{product.productname}</h1>
            <div>[reviews]</div>
            <div className={style.tagLine}>
              Vegan | Aspartame-Free | Sugar-Free | Gluten-Free
            </div>
            <div className={style.description}>
              When it comes to taking on big goals, we need energy levels that
              match the mission. Neuro Gum Energy &amp; Focus is specially
              formulated with natural caffeine, L-theanine, and B-vitamins to
              sustain the mental endurance necessary to stay focused on your
              goals.
            </div>
            <div><strong>Flavor:</strong> Peppermint</div>
          </div>
        </TwoColFlex>
      </div>
      ))}


        
      </MaxContentWidth>
    </>
  );
};

export default SingleProduct;
